mapper.select:
select codigo_recurso as codigo, nombre_recurso as nombre, descripcion_recurso as descripcion, padre_recurso as codigoPadre, cod_estado as estado, url_recurso as url from sid_seg_recurso where 1=1
and codigo_recurso=#{codigo}
and UPPER(nom_rol) LIKE '%'||UPPER(#{nombre})||'%'
and padre_recurso=#{codigoPadre}
and estado=#{estado}
order by codigo

mapper.delete:
delete sid_seg_rol_recurso
where codigo_rol = #{rol.codigo}
and   codigo_recurso = #{recurso.codigo}
