mapper.select:
select codigo_rol as codigo, nom_rol as nombre, des_rol as descripcion, cod_estado as estado from sid_seg_rol where 1=1
and codigo_rol=#{codigo}
and UPPER(nom_rol) LIKE '%'||UPPER(#{nombre})||'%'
and estado=#{estado}
order by codigo

mapper.delete:
delete sid_seg_usuario_rol
where codigo_rol = #{rol.codigo}
and codigo_usuario = #{usuario.codigo}
