mapper.update:
update sid_seg_usuario
set
nom_usuario = UPPER(#{nombre}),
ape_paterno = UPPER(#{apellidoPaterno}),
ape_materno = UPPER(#{apellidoMaterno})

    ,num_dni = #{dni}


    ,est_usuario = #{estado}


    ,des_email =#{email}


    ,tel_movil = #{telefonoMovil}


    ,tel_fijo = #{telefonoFijo}


    ,c_ruta = #{ruta}


    ,c_cargo = #{cargo}


    ,N_ID_OD = #{codigoOD}


    ,c_iddepartamento = #{idDepartamento}

where codigo_usuario = #{codigo}

mapper.select:
select u.codigo_usuario as codigo, u.nom_usuario as nombre, u.ape_paterno as apellidoPaterno, u.ape_materno as apellidoMaterno, u.num_dni as dni, u.est_usuario as estado, u.num_dni as clave, u.des_email as email, u.tel_movil as telefonoMovil, u.tel_fijo as telefonoFijo, u.flag_clave_inicial as flagClaveInicial, u.c_cargo as cargo, u.c_ruta as ruta, u.N_ID_OD as codigoOD from sid_seg_usuario u INNER JOIN SID_SEG_USUARIO_ROL B ON B.CODIGO_USUARIO = u.CODIGO_USUARIO WHERE u.N_ID_OD = #{codigoOD} AND B.CODIGO_ROL NOT IN ('ROL0000010')
AND B.CODIGO_ROL = #{rol}
ORDER BY u.nom_usuario

select u.codigo_usuario as codigo, u.nom_usuario as nombre, u.ape_paterno as apellidoPaterno, u.ape_materno as apellidoMaterno, u.num_dni as dni, u.est_usuario as estado, u.num_dni as clave, u.des_email as email, u.tel_movil as telefonoMovil, u.tel_fijo as telefonoFijo, u.flag_clave_inicial as flagClaveInicial, u.c_cargo as cargo, u.c_ruta as ruta, u.N_ID_OD as codigoOD from sid_seg_usuario u INNER JOIN SID_SEG_USUARIO_ROL B ON B.CODIGO_USUARIO = u.CODIGO_USUARIO WHERE B.CODIGO_ROL IN ('ROL0000011')
AND u.N_ID_OD = #{codigoOD}
ORDER BY u.nom_usuario
