SELECT A.* FROM ( SELECT B.*, ROWNUM numfila FROM ( SELECT B.C_NOMBREPARAMETRO NOMBREPRIMERNIVEL, '' NOMBRESEGUNDORNIVEL, '' NOMBRETERCERNIVEL, '' NOMBRECUARTONIVEL, '' NOMBREQUINTONIVEL, '' NOMBRESEXTONIVEL, COUNT(DISTINCT C.C_NUMEROEXPEDIENTE) TOTAL, COUNT(DISTINCT D.C_NUMEROEXPEDIENTE) TOTAL_QUEJA, COUNT(DISTINCT E.C_NUMEROEXPEDIENTE) TOTAL_CONSULTA, COUNT(DISTINCT F.C_NUMEROEXPEDIENTE) TOTAL_PETITORIO, COUNT(DISTINCT D1.C_NUMEROEXPEDIENTE) TOTAL_QUEJA_CONCLUIDO, COUNT(DISTINCT E1.C_NUMEROEXPEDIENTE) TOTAL_CONSULTA_CONCLUIDO, COUNT(DISTINCT F1.C_NUMEROEXPEDIENTE) TOTAL_PETITORIO_CONCLUIDO, COUNT(DISTINCT D2.C_NUMEROEXPEDIENTE) TOTAL_QUEJA_TRAMITE, COUNT(DISTINCT E2.C_NUMEROEXPEDIENTE) TOTAL_CONSULTA_TRAMITE, COUNT(DISTINCT F2.C_NUMEROEXPEDIENTE) TOTAL_PETITORIO_TRAMITE FROM SID_REG_EXPEDIENTE_CLASI A INNER JOIN SID_REG_CLASIFICACION B ON A.N_ID_PRIMERNIVEL = B.N_ID_CLASIFICACION INNER JOIN SID_REG_EXPEDIENTE X ON X.C_NUMEROEXPEDIENTE = A.C_NUMEROEXPEDIENTE AND X.C_ESTADO = 'A' LEFT JOIN (SELECT * FROM SID_REG_EXPEDIENTE WHERE C_ESTADO = 'A' AND TRIM(C_NUMEROEXPEDIENTE) IS NOT NULL) C ON C.C_NUMEROEXPEDIENTE = A.C_NUMEROEXPEDIENTE LEFT JOIN (SELECT * FROM SID_REG_EXPEDIENTE WHERE C_TIPOCLASIFICACION = '01' AND C_ESTADO = 'A' AND TRIM(C_NUMEROEXPEDIENTE) IS NOT NULL) D ON D.C_NUMEROEXPEDIENTE = A.C_NUMEROEXPEDIENTE LEFT JOIN (SELECT * FROM SID_REG_EXPEDIENTE WHERE C_TIPOCLASIFICACION = '01' AND C_ESTADO = 'A' AND TRIM(C_NUMEROEXPEDIENTE) IS NOT NULL AND C_ESTADOGENERAL = 'C') D1 ON D1.C_NUMEROEXPEDIENTE = A.C_NUMEROEXPEDIENTE LEFT JOIN (SELECT * FROM SID_REG_EXPEDIENTE WHERE C_TIPOCLASIFICACION = '01' AND C_ESTADO = 'A' AND TRIM(C_NUMEROEXPEDIENTE) IS NOT NULL AND C_ESTADOGENERAL = 'A') D2 ON D2.C_NUMEROEXPEDIENTE = A.C_NUMEROEXPEDIENTE LEFT JOIN (SELECT * FROM SID_REG_EXPEDIENTE WHERE C_TIPOCLASIFICACION = '02' AND C_ESTADO = 'A' AND TRIM(C_NUMEROEXPEDIENTE) IS NOT NULL) E ON E.C_NUMEROEXPEDIENTE = A.C_NUMEROEXPEDIENTE LEFT JOIN (SELECT * FROM SID_REG_EXPEDIENTE W