GROUP BY A.CODIGO_USUARIO, A.NOM_USUARIO, A.APE_PATERNO, A.APE_MATERNO, A.C_CARGO, B.C_NOMBRE ) B ) A WHERE a.numfila BETWEEN #{ini} AND #{fin}

SELECT A.CODIGO_USUARIO, A.NOM_USUARIO, A.APE_PATERNO, A.APE_MATERNO, A.C_CARGO, B.C_NOMBRE, COUNT(DISTINCT C.C_NUMEROEXPEDIENTE) TOTAL, COUNT(DISTINCT D.C_NUMEROEXPEDIENTE) TOTAL_QUEJA, COUNT(DISTINCT E.C_NUMEROEXPEDIENTE) TOTAL_CONSULTA, COUNT(DISTINCT F.C_NUMEROEXPEDIENTE) TOTAL_PETITORIO, COUNT(DISTINCT D1.C_NUMEROEXPEDIENTE) TOTAL_QUEJA_CONCLUIDO, COUNT(DISTINCT E1.C_NUMEROEXPEDIENTE) TOTAL_CONSULTA_CONCLUIDO, COUNT(DISTINCT F1.C_NUMEROEXPEDIENTE) TOTAL_PETITORIO_CONCLUIDO, COUNT(DISTINCT D2.C_NUMEROEXPEDIENTE) TOTAL_QUEJA_TRAMITE, COUNT(DISTINCT E2.C_NUMEROEXPEDIENTE) TOTAL_CONSULTA_TRAMITE, COUNT(DISTINCT F2.C_NUMEROEXPEDIENTE) TOTAL_PETITORIO_TRAMITE FROM SID_SEG_USUARIO A INNER JOIN SID_SEG_OFICINA_DEFENSORIAL B ON A.N_ID_OD = B.N_ID_OD INNER JOIN SID_REG_EXPEDIENTE C ON C.C_USUREGISTRO = A.CODIGO_USUARIO AND TRIM(C.C_NUMEROEXPEDIENTE) IS NOT NULL AND C.C_ESTADO = 'A' LEFT JOIN SID_REG_EXPEDIENTE D ON D.C_USUREGISTRO = A.CODIGO_USUARIO AND D.C_ESTADO = 'A' AND TRIM(D.C_NUMEROEXPEDIENTE) IS NOT NULL AND D.C_TIPOCLASIFICACION = '01' LEFT JOIN SID_REG_EXPEDIENTE E ON E.C_USUREGISTRO = A.CODIGO_USUARIO AND E.C_ESTADO = 'A' AND TRIM(E.C_NUMEROEXPEDIENTE) IS NOT NULL AND E.C_TIPOCLASIFICACION = '02' LEFT JOIN SID_REG_EXPEDIENTE F ON F.C_USUREGISTRO = A.CODIGO_USUARIO AND F.C_ESTADO = 'A' AND TRIM(F.C_NUMEROEXPEDIENTE) IS NOT NULL AND F.C_TIPOCLASIFICACION = '03' LEFT JOIN SID_REG_EXPEDIENTE D1 ON D1.C_TIPOCLASIFICACION = '01' AND D1.C_ESTADO = 'A' AND TRIM(D1.C_NUMEROEXPEDIENTE) IS NOT NULL AND D1.C_ESTADOGENERAL = 'C' AND D1.C_USUREGISTRO = A.CODIGO_USUARIO LEFT JOIN SID_REG_EXPEDIENTE D2 ON D2.C_TIPOCLASIFICACION = '01' AND D2.C_ESTADO = 'A' AND TRIM(D2.C_NUMEROEXPEDIENTE) IS NOT NULL AND D2.C_ESTADOGENERAL = 'A' AND D2.C_USUREGISTRO = A.CODIGO_USUARIO LEFT JOIN SID_REG_EXPEDIENTE E1 ON E1.C_TIPOCLASIFICACION = '02' AND E1.C_ESTADO = 'A' AND TRIM(E1.C_NUMEROEXPEDIENTE) IS NOT NULL AND E1.C_ESTADOGENERAL = 'C' AND E1.C_USUREGISTRO = A.CODIGO_USUARIO LEFT JOIN SID_REG_EXPEDIENTE E2 ON E2.C_TIPOCLASIFICACION = '02' AND E2.C_ESTADO = 'A' AND TRIM(E2.C_NUMEROEXPEDIENTE) IS NOT NULL AND E2.C_ESTADOGENERAL = 'C' AND E2.C_USUREGISTRO = A.CODIGO_USUARIO LEFT JOIN SID_REG_EXPEDIENTE F1 ON F1.C_TIPOCLASIFICACION = '03' AND F1.C_ESTADO = 'A' AND TRIM(F1.C_NUMEROEXPEDIENTE) IS NOT NULL AND F1.C_ESTADOGENERAL = 'C' AND F1.C_USUREGISTRO = A.CODIGO_USUARIO LEFT JOIN SID_REG_EXPEDIENTE F2 ON F2.C_TIPOCLASIFICACION = '03' AND F2.C_ESTADO = 'A' AND TRIM(F2.C_NUMEROEXPEDIENTE) IS NOT NULL AND F2.C_ESTADOGENERAL = 'C' AND F2.C_USUREGISTRO = A.CODIGO_USUARIO WHERE 1 = 1
AND TO_NUMBER(TO_CHAR(C.D_FECHAINGRESO, 'YYYYMMDD')) > TO_NUMBER(#{fechaIngresoDesdeString})-1 AND TO_NUMBER(TO_CHAR(C.D_FECHAINGRESO, 'YYYYMMDD')) < TO_NUMBER(#{fechaIngresoHastaString})+1
AND TO_NUMBER(TO_CHAR(C.D_FECHAMODIFICACION, 'YYYYMMDD')) > TO_NUMBER(#{fechaConclusionDesdeString})-1 AND TO_NUMBER(TO_CHAR(C.D_FECHAMODIFICACION, 'YYYYMMDD')) < TO_NUMBER(#{fechaConclusionHastaString})+1
AND C.C_TIPOCLASIFICACION = #{tipoExpediente}
AND C.C_TIPOINGRESO = #{canalIngreso}
AND C.C_USUREGISTRO = #{usuarioCodigo}
GROUP BY A.CODIGO_USUARIO, A.NOM_USUARIO, A.APE_PATERNO, A.APE_MATERNO, A.C_CARGO, B.C_NOMBRE

SELECT A.* FROM ( SELECT B.*, ROWNUM numfila FROM ( SELECT B.C_NOMBRE, COUNT(DISTINCT C.C_NUMEROEXPEDIENTE) TOTAL, COUNT(DISTINCT D.C_NUMEROEXPEDIENTE) TOTAL_QUEJA, COUNT(DISTINCT E.C_NUMEROEXPEDIENTE) TOTAL_CONSULTA, COUNT(DISTINCT F.C_NUMEROEXPEDIENTE) TOTAL_PETITORIO, COUNT(DISTINCT D1.C_NUMEROEXPEDIENTE) TOTAL_QUEJA_CONCLUIDO, COUNT(DISTINCT E1.C_NUMEROEXPEDIENTE) TOTAL_CONSULTA_CONCLUIDO, COUNT(DISTINCT F1.C_NUMEROEXPEDIENTE) TOTAL_PETITORIO_CONCLUIDO, COUNT(DISTINCT D2.C_NUMEROEXPEDIENTE) TOTAL_QUEJA_TRAMITE, COUNT(DISTINCT E2.C_NUMEROEXPEDIENTE) TOTAL_CONSULTA_TRAMITE, COUNT(DISTINCT F2.C_NUMEROEXPEDIENTE) TOTAL_PETITORIO_TRAMITE FROM SID_SEG_OFICINA_DEFENSORIAL B INNER JOIN SID_REG_EXPEDIENTE C ON C.N_CODIGOOD = B.N_ID_OD AND TRIM(C.C_NUMEROEXPEDIENTE) IS NOT NULL AND C.C_ESTADO = 'A' LEFT JOIN SID_REG_EXPEDIENTE D ON D.N_CODIGOOD = B.N_ID_OD AND D.C_ESTADO = 'A' AND TRIM(D.C_NUMEROEXPEDIENTE) IS NOT NULL AND D.C_TIPOCLASIFICACION = '01' LEFT JOIN SID_REG_EXPEDIENTE E ON E.N_CODIGOOD = B.N_ID_OD AND E.C_ESTADO = 'A' AND TRIM(E.C_NUMEROEXPEDIENTE) IS NOT NULL AND E.C_TIPOCLASIFICACION = '02' LEFT JOIN SID_REG_EXPEDIENTE F ON F.N_CODIGOOD = B.N_ID_OD AND F.C_ESTADO = 'A' AND TRIM(F.C_NUMEROEXPEDIENTE) IS NOT NULL AND F.C_TIPOCLASIFICACION = '03' LEFT JOIN SID_REG_EXPEDIENTE D1 ON D1.C_TIPOCLASIFICACION = '01' AND D1.C_ESTADO = 'A' AND TRIM(D1.C_NUMEROEXPEDIENTE) IS NOT NULL AND D1.C_ESTADOGENERAL = 'C' AND D1.N_CODIGOOD = B.N_ID_OD LEFT JOIN SID_REG_EXPEDIENTE D2 ON D2.C_TIPOCLASIFICACION = '01' AND D2.C_ESTADO = 'A' AND TRIM(D2.C_NUMEROEXPEDIENTE) IS NOT NULL AND D2.C_ESTADOGENERAL = 'A' AND D2.N_CODIGOOD = B.N_ID_OD LEFT JOIN SID_REG_EXPEDIENTE E1 ON E1.C_TIPOCLASIFICACION = '02' AND E1.C_ESTADO = 'A' AND TRIM(E1.C_NUMEROEXPEDIENTE) IS NOT NULL AND E1.C_ESTADOGENERAL = 'C' AND E1.N_CODIGOOD = B.N_ID_OD LEFT JOIN SID_REG_EXPEDIENTE E2 ON E2.C_TIPOCLASIFICACION = '02' AND E2.C_ESTADO = 'A' AND TRIM(E2.C_NUMEROEXPEDIENTE) IS NOT NULL AND E2.C_ESTADOGENERAL = 'C' AND E2.N_CODIGOOD = B.N_ID_OD LEFT JOIN SID_REG_EXPEDIENTE F1 ON F1.C_TIPOCLASIFICACION = '03' AND F1.C_ESTADO = 'A' AND TRIM(F1.C_NUMEROEXPEDIENTE) IS NOT NULL AND F1.C_ESTADOGENERAL = 'C' AND F1.N_CODIGOOD = B.N_ID_OD LEFT JOIN SID_REG_EXPEDIENTE F2 ON F2.C_TIPOCLASIFICACION = '03' AND F2.C_ESTADO = 'A' AND TRIM(F2.C_NUMEROEXPEDIENTE) IS NOT NULL AND F2.C_ESTADOGENERAL = 'C' AND F2.N_CODIGOOD = B.N_ID_OD WHERE 1 = 1
AND TO_NUMBER(TO_CHAR(C.D_FECHAINGRESO, 'YYYYMMDD')) > TO_NUMBER(#{fechaIngresoDesdeString})-1 AND TO_NUMBER(TO_CHAR(C.D_FECHAINGRESO, 'YYYYMMDD')) < TO_NUMBER(#{fechaIngresoHastaString})+1
AND TO_NUMBER(TO_CHAR(C.D_FECHAMODIFICACION, 'YYYYMMDD')) > TO_NUMBER(#{fechaConclusionDesdeString})-1 AND TO_NUMBER(TO_CHAR(C.D_FECHAMODIFICACION, 'YYYYMMDD')) < TO_NUMBER(#{fechaConclusionHastaString})+1
AND C.C_TIPOCLASIFICACION = #{tipoExpediente}
AND C.C_TIPOINGRESO = #{canalIngreso}
AND C.C_USUREGISTRO = #{usuarioCodigo}
GROUP BY B.C_NOMBRE ) B ) A WHERE a.numfila BETWEEN #{ini} AND #{fin}

SELECT B.C_NOMBRE, COUNT(DISTINCT C.C_NUMEROEXPEDIENTE) TOTAL, COUNT(DISTINCT D.C_NUMEROEXPEDIENTE) TOTAL_QUEJA, COUNT(DISTINCT E.C_NUMEROEXPEDIENTE) TOTAL_CONSULTA, COUNT(DISTINCT F.C_NUMEROEXPEDIENTE) TOTAL_PETITORIO, COUNT(DISTINCT D1.C_NUMEROEXPEDIENTE) TOTAL_QUEJA_CONCLUIDO, COUNT(DISTINCT E1.C_NUMEROEXPEDIENTE) TOTAL_CONSULTA_CONCLUIDO, COUNT(DISTINCT F1.C_NUMEROEXPEDIENTE) TOTAL_PETITORIO_CONCLUIDO, COUNT(DISTINCT D2.C_NUMEROEXPEDIENTE) TOTAL_QUEJA_TRAMITE, COUNT(DISTINCT E2.C_NUMEROEXPEDIENTE) TOTAL_CONSULTA_TRAMITE, COUNT(DISTINCT F2.C_NUMEROEXPEDIENTE) TOTAL_PETITORIO_TRAMITE FROM SID_SEG_OFICINA_DEFENSORIAL B INNER JOIN SID_REG_EXPEDIENTE C ON C.N_CODIGOOD = B.N_ID_OD AND TRIM(C.C_NUMEROEXPEDIENTE) IS NOT NULL AND C.C_ESTADO = 'A' LEFT JOIN SID_REG_EXPEDIENTE D ON D.N_CODIGOOD = B.N_ID_OD AND D.C_ESTADO = 'A' AND TRIM(D.C_NUMEROEXPEDIENTE) IS NOT NULL AND D.C_TIPOCLASIFICACION = '01' LEFT JOIN SID_REG_EXPEDIENTE E ON E.N_CODIGOOD = B.N_ID_OD AND E.C_ESTADO = 'A' AND TRIM(E.C_NUMEROEXPEDIENTE) IS NOT NULL AND E.C_TIPOCLASIFICACION = '02' LEFT JOIN SID_REG_EXPEDIENTE F ON F.N_CODIGOOD = B.N_ID_OD AND F.C_ESTADO = 'A' AND TRIM(F.C_NUMEROEXPEDIENTE) IS NOT NULL AND F.C_TIPOCLASIFICACION = '03' LEFT JOIN SID_REG_EXPEDIENTE D1 ON D1.C_TIPOCLASIFICACION = '01' AND D1.C_ESTADO = 'A' AND TRIM(D1.C_NUMEROEXPEDIENTE) IS NOT NULL AND D1.C_ESTADOGENERAL = 'C' AND D1.N_CODIGOOD = B.N_ID_OD LEFT JOIN SID_REG_EXPEDIENTE D2 ON D2.C_TIPOCLASIFICACION = '01' AND D2.C_ESTADO = 'A' AND TRIM(D2.C_NUMEROEXPEDIENTE) IS NOT NULL AND D2.C_ESTADOGENERAL = 'A' AND D2.N_CODIGOOD = B.N_ID_OD LEFT JOIN SID_REG_EXPEDIENTE E1 ON E1.C_TIPOCLASIFICACION = '02' AND E1.C_ESTADO = 'A' AND TRIM(E1.C_NUMEROEXPEDIENTE) IS NOT NULL AND E1.C_ESTADOGENERAL = 'C' AND E1.N_CODIGOOD = B.N_ID_OD LEFT JOIN SID_REG_EXPEDIENTE E2 ON E2.C_TIPOCLASIFICACION = '02' AND E2.C_ESTADO = 'A' AND TRIM(E2.C_NUMEROEXPEDIENTE) IS NOT NULL AND E2.C_ESTADOGENERAL = 'C' AND E2.N_CODIGOOD = B.N_ID_OD LEFT JOIN SID_REG_EXPEDIENTE F1 ON F1.C_TIPOCLASIFICACION = '03' AND F1.C_ESTADO = 'A' AND TRIM(F1.C_NUMEROEXPEDIENTE) IS NOT NULL AND F1.C_ESTADOGENERAL = 'C' AND F1.N_CODIGOOD = B.N_ID_OD LEFT JOIN SID_REG_EXPEDIENTE F2 ON F2.C_TIPOCLASIFICACION = '03' AND F2.C_ESTADO = 'A' AND TRIM(F2.C_NUMEROEXPEDIENTE) IS NOT NULL AND F2.C_ESTADOGENERAL = 'C' AND F2.N_CODIGOOD = B.N_ID_OD WHERE 1 = 1
AND TO_NUMBER(TO_CHAR(C.D_FECHAINGRESO, 'YYYYMMDD')) > TO_NUMBER(#{fechaIngresoDesdeString})-1 AND TO_NUMBER(TO_CHAR(C.D_FECHAINGRESO, 'YYYYMMDD')) < TO_NUMBER(#{fechaIngresoHastaString})+1
AND TO_NUMBER(TO_CHAR(C.D_FECHAMODIFICACION, 'YYYYMMDD')) > TO_NUMBER(#{fechaConclusionDesdeString})-1 AND TO_NUMBER(TO_CHAR(C.D_FECHAMODIFICACION, 'YYYYMMDD')) < TO_NUMBER(#{fechaConclusionHastaString})+1
AND C.C_TIPOCLASIFICACION = #{tipoExpediente}
AND C.C_TIPOINGRESO = #{canalIngreso}
AND C.C_USUREGISTRO = #{usuarioCodigo}
GROUP BY B.C_NOMBRE

SELECT A.* FROM ( SELECT B.*, ROWNUM numfila FROM ( SELECT DISTINCT A.C_NUMEROEXPEDIENTE, P.NOMBRE_PARAMETRO TIPOEXPEDIENTE, B.D_FECHA D_FECHAREGISTRO, C.D_FECHA D_FECHAINGRESO, CASE WHEN NVL(C.C_CODIGO, '0') = '0' THEN 'No' ELSE 'Si' END C_RESPUESTA, 'CONSULTA' C_TIPOACCION, E.C_NOMBRECOMPLETO C_DEPENDENCIA_ORIGEN, F.C_NOMBRECOMPLETO C_DEPENDENCIA_DESTINO, B.C_CODIGO C_CODIGOGESTION FROM SID_REG_EXPEDIENTE A INNER JOIN (SELECT * FROM SID_REG_EXPEDIENTE_CONSULTA WHERE C_ETAPA = 1) B ON A.C_NUMEROEXPEDIENTE = B.C_NUMEROEXPEDIENTE INNER JOIN SID_PARAMETRO P ON A.C_TIPOCLASIFICACION = P.VALOR_PARAMETRO AND P.PADRE_PARAMETRO = 10 LEFT JOIN (SELECT * FROM SID_REG_EXPEDIENTE_CONSULTA WHERE C_ETAPA = 6) C ON A.C_NUMEROEXPEDIENTE = C.C_NUMEROEXPEDIENTE INNER JOIN SID_SEG_USUARIO D ON B.C_CODIGOUSUARIO = D.CODIGO_USUARIO INNER JOIN SID_SEG_OFICINA_DEFENSORIAL E ON E.N_ID_OD = D.N_ID_OD LEFT JOIN SID_SEG_OFICINA_DEFENSORIAL F ON F.N_ID_OD = B.N_ID_AD WHERE A.C_ESTADO = 'A'
AND TO_NUMBER(TO_CHAR(A.D_FECHAINGRESO, 'YYYYMMDD')) > TO_NUMBER(#{fechaIngresoDesdeString})-1 AND TO_NUMBER(TO_CHAR(A.D_FECHAINGRESO, 'YYYYMMDD')) < TO_NUMBER(#{fechaIngresoHastaString})+1
AND TO_NUMBER(TO_CHAR(A.D_FECHAMODIFICACION, 'YYYYMMDD')) > TO_NUMBER(#{fechaConclusionDesdeString})-1 AND TO_NUMBER(TO_CHAR(A.D_FECHAMODIFICACION, 'YYYYMMDD')) < TO_NUMBER(#{fechaConclusionHastaString})+1
AND A.C_TIPOCLASIFICACION = #{tipoExpediente}
AND A.C_TIPOINGRESO = #{canalIngreso}
AND A.C_USUREGISTRO = #{usuarioCodigo}
UNION ALL SELECT DISTINCT A.C_NUMEROEXPEDIENTE, P.NOMBRE_PARAMETRO TIPOEXPEDIENTE, B.D_FECHAACCION D_FECHAREGISTRO, C.D_FECHAACCION D_FECHAINGRESO, CASE WHEN NVL(C.N_ID_DERIVACION, '0') = '0' THEN 'No' ELSE 'Si' END C_RESPUESTA, 'DERIVACION' C_TIPOACCION, E.C_NOMBRECOMPLETO C_DEPENDENCIA_ORIGEN, F.C_NOMBRECOMPLETO C_DEPENDENCIA_DESTINO, B.C_CODIGOUSUARIO||B.N_ID_DERIVACION C_CODIGOGESTION FROM SID_REG_EXPEDIENTE A INNER JOIN (SELECT * FROM SID_REG_EXPEDIENTE_DERIVACION WHERE C_ETAPA = 1) B ON A.C_NUMEROEXPEDIENTE = B.C_NUMEROEXPEDIENTE INNER JOIN SID_PARAMETRO P ON A.C_TIPOCLASIFICACION = P.VALOR_PARAMETRO AND P.PADRE_PARAMETRO = 10 LEFT JOIN (SELECT * FROM SID_REG_EXPEDIENTE_DERIVACION WHERE C_ETAPA = 3) C ON A.C_NUMEROEXPEDIENTE = C.C_NUMEROEXPEDIENTE INNER JOIN SID_SEG_USUARIO D ON B.C_CODIGOUSUARIO = D.CODIGO_USUARIO INNER JOIN SID_SEG_OFICINA_DEFENSORIAL E ON E.N_ID_OD = D.N_ID_OD LEFT JOIN SID_SEG_OFICINA_DEFENSORIAL F ON F.N_ID_OD = B.N_ID_OD WHERE A.C_ESTADO = 'A'
AND TO_NUMBER(TO_CHAR(A.D_FECHAINGRESO, 'YYYYMMDD')) > TO_NUMBER(#{fechaIngresoDesdeString})-1 AND TO_NUMBER(TO_CHAR(A.D_FECHAINGRESO, 'YYYYMMDD')) < TO_NUMBER(#{fechaIngresoHastaString})+1
AND TO_NUMBER(TO_CHAR(A.D_FECHAMODIFICACION, 'YYYYMMDD')) > TO_NUMBER(#{fechaConclusionDesdeString})-1 AND TO_NUMBER(TO_CHAR(A.D_FECHAMODIFICACION, 'YYYYMMDD')) < TO_NUMBER(#{fechaConclusionHastaString})+1
AND A.C_TIPOCLASIFICACION = #{tipoExpediente}
AND A.C_TIPOINGRESO = #{canalIngreso}
AND A.C_USUREGISTRO = #{usuarioCodigo}
UNION ALL SELECT DISTINCT A.C_NUMEROEXPEDIENTE, P.NOMBRE_PARAMETRO TIPOEXPEDIENTE, B.D_FECHAACCION D_FECHAREGISTRO, C.D_FECHAACCION D_FECHAINGRESO, CASE WHEN NVL(C.N_ID_AMPLIACION, '0') = '0' THEN 'No' ELSE 'Si' END C_RESPUESTA, 'AMPLIACION' C_TIPOACCION, E.C_NOMBRECOMPLETO C_DEPENDENCIA_ORIGEN, F.C_NOMBRECOMPLETO C_DEPENDENCIA_DESTINO, B.C_CODIGOUSUARIO||B.N_ID_AMPLIACION C_CODIGOGESTION FROM SID_REG_EXPEDIENTE A INNER JOIN (SELECT * FROM SID_REG_EXPEDIENTE_AMPLIACION WHERE N_ETAPA = 1) B ON A.N_IDEXPEDIENTE = B.N_IDEXPEDIENTE INNER JOIN SID_PARAMETRO P ON A.C_TIPOCLASIFICACION = P.VALOR_PARAMETRO AND P.PADRE_PARAMETRO = 10 LEFT JOIN (SELECT * FROM SID_REG_EXPEDIENTE_AMPLIACION WHERE N_ETAPA = 3) C ON A.N_IDEXPEDIENTE = C.N_IDEXPEDIENTE INNER JOIN SID_SEG_USUARIO D ON B.C_CODIGOUSUARIO = D.CODIGO_USUARIO INNER JOIN SID_SEG_OFICINA_DEFENSORIAL E ON E.N_ID_OD = D.N_ID_OD LEFT JOIN SID_SEG_OFICINA_DEFENSORIAL F ON F.N_ID_OD = B.N_ID_AD WHERE 1 = 1
AND TO_NUMBER(TO_CHAR(A.D_FECHAINGRESO, 'YYYYMMDD')) > TO_NUMBER(#{fechaIngresoDesdeString})-1 AND TO_NUMBER(TO_CHAR(A.D_FECHAINGRESO, 'YYYYMMDD')) < TO_NUMBER(#{fechaIngresoHastaString})+1
AND TO_NUMBER(TO_CHAR(A.D_FECHAMODIFICACION, 'YYYYMMDD')) > TO_NUMBER(#{fechaConclusionDesdeString})-1 AND TO_NUMBER(TO_CHAR(A.D_FECHAMODIFICACION, 'YYYYMMDD')) < TO_NUMBER(#{fechaConclusionHastaString})+1
AND A.C_TIPOCLASIFICACION = #{tipoExpediente}
AND A.C_TIPOINGRESO = #{canalIngreso}
AND A.C_USUREGISTRO = #{usuarioCodigo}
UNION ALL SELECT DISTINCT A.C_NUMEROEXPEDIENTE, P.NOMBRE_PARAMETRO TIPOEXPEDIENTE, B.D_FECHAACCION D_FECHAREGISTRO, C.D_FECHAACCION D_FECHAINGRESO, CASE WHEN NVL(C.N_ID_SUSPENCION, '0') = '0' THEN 'No' ELSE 'Si' END C_RESPUESTA, 'SUSPENCION' C_TIPOACCION, E.C_NOMBRECOMPLETO C_DEPENDENCIA_ORIGEN, F.C_NOMBRECOMPLETO C_DEPENDENCIA_DESTINO, B.C_CODIGOUSUARIO||B.N_ID_SUSPENCION C_CODIGOGESTION FROM SID_REG_EXPEDIENTE A INNER JOIN (SELECT * FROM SID_REG_EXPEDIENTE_SUSPENCION WHERE N_ETAPA = 1) B ON A.N_IDEXPEDIENTE = B.N_IDEXPEDIENTE INNER JOIN SID_PARAMETRO P ON A.C_TIPOCLASIFICACION = P.VALOR_PARAMETRO AND P.PADRE_PARAMETRO = 10 LEFT JOIN (SELECT * FROM SID_REG_EXPEDIENTE_SUSPENCION WHERE N_ETAPA = 3) C ON A.N_IDEXPEDIENTE = C.N_IDEXPEDIENTE INNER JOIN SID_SEG_USUARIO D ON B.C_CODIGOUSUARIO = D.CODIGO_USUARIO INNER JOIN SID_SEG_OFICINA_DEFENSORIAL E ON E.N_ID_OD = D.N_ID_OD LEFT JOIN SID_SEG_OFICINA_DEFENSORIAL F ON F.N_ID_OD = B.N_ID_AD WHERE 1 = 1
AND TO_NUMBER(TO_CHAR(A.D_FECHAINGRESO, 'YYYYMMDD')) > TO_NUMBER(#{fechaIngresoDesdeString})-1 AND TO_NUMBER(TO_CHAR(A.D_FECHAINGRESO, 'YYYYMMDD')) < TO_NUMBER(#{fechaIngresoHastaString})+1
AND TO_NUMBER(TO_CHAR(A.D_FECHAMODIFICACION, 'YYYYMMDD')) > TO_NUMBER(#{fechaConclusionDesdeString})-1 AND TO_NUMBER(TO_CHAR(A.D_FECHAMODIFICACION, 'YYYYMMDD')) < TO_NUMBER(#{fechaConclusionHastaString})+1
AND A.C_TIPOCLASIFICACION = #{tipoExpediente}
AND A.C_TIPOINGRESO = #{canalIngreso}
AND A.C_USUREGISTRO = #{usuarioCodigo}
) B ) A WHERE a.numfila BETWEEN #{ini} AND #{fin}

SELECT DISTINCT A.C_NUMEROEXPEDIENTE, P.NOMBRE_PARAMETRO TIPOEXPEDIENTE, B.D_FECHA D_FECHAREGISTRO, C.D_FECHA D_FECHAINGRESO, CASE WHEN NVL(C.C_CODIGO, '0') = '0' THEN 'No' ELSE 'Si' END C_RESPUESTA, 'CONSULTA' C_TIPOACCION, E.C_NOMBRECOMPLETO C_DEPENDENCIA_ORIGEN, F.C_NOMBRECOMPLETO C_DEPENDENCIA_DESTINO, B.C_CODIGO C_CODIGOGESTION FROM SID_REG_EXPEDIENTE A INNER JOIN (SELECT * FROM SID_REG_EXPEDIENTE_CONSULTA WHERE C_ETAPA = 1) B ON A.C_NUMEROEXPEDIENTE = B.C_NUMEROEXPEDIENTE INNER JOIN SID_PARAMETRO P ON A.C_TIPOCLASIFICACION = P.VALOR_PARAMETRO AND P.PADRE_PARAMETRO = 10 LEFT JOIN (SELECT * FROM SID_REG_EXPEDIENTE_CONSULTA WHERE C_ETAPA = 6) C ON A.C_NUMEROEXPEDIENTE = C.C_NUMEROEXPEDIENTE INNER JOIN SID_SEG_USUARIO D ON B.C_CODIGOUSUARIO = D.CODIGO_USUARIO INNER JOIN SID_SEG_OFICINA_DEFENSORIAL E ON E.N_ID_OD = D.N_ID_OD LEFT JOIN SID_SEG_OFICINA_DEFENSORIAL F ON F.N_ID_OD = B.N_ID_AD WHERE A.C_ESTADO = 'A'
AND TO_NUMBER(TO_CHAR(A.D_FECHAINGRESO, 'YYYYMMDD')) > TO_NUMBER(#{fechaIngresoDesdeString})-1 AND TO_NUMBER(TO_CHAR(A.D_FECHAINGRESO, 'YYYYMMDD')) < TO_NUMBER(#{fechaIngresoHastaString})+1
AND TO_NUMBER(TO_CHAR(A.D_FECHAMODIFICACION, 'YYYYMMDD')) > TO_NUMBER(#{fechaConclusionDesdeString})-1 AND TO_NUMBER(TO_CHAR(A.D_FECHAMODIFICACION, 'YYYYMMDD')) < TO_NUMBER(#{fechaConclusionHastaString})+1
AND A.C_TIPOCLASIFICACION = #{tipoExpediente}
AND A.C_TIPOINGRESO = #{canalIngreso}
AND A.C_USUREGISTRO = #{usuarioCodigo}
UNION ALL SELECT DISTINCT A.C_NUMEROEXPEDIENTE, P.NOMBRE_PARAMETRO TIPOEXPEDIENTE, B.D_FECHAACCION D_FECHAREGISTRO, C.D_FECHAACCION D_FECHAINGRESO, CASE WHEN NVL(C.N_ID_DERIVACION, '0') = '0' THEN 'No' ELSE 'Si' END C_RESPUESTA, 'DERIVACION' C_TIPOACCION, E.C_NOMBRECOMPLETO C_DEPENDENCIA_ORIGEN, F.C_NOMBRECOMPLETO C_DEPENDENCIA_DESTINO, B.C_CODIGOUSUARIO||B.N_ID_DERIVACION C_CODIGOGESTION FROM SID_REG_EXPEDIENTE A INNER JOIN (SELECT * FROM SID_REG_EXPEDIENTE_DERIVACION WHERE C_ETAPA = 1) B ON A.C_NUMEROEXPEDIENTE = B.C_NUMEROEXPEDIENTE INNER JOIN SID_PARAMETRO P ON A.C_TIPOCLASIFICACION = P.VALOR_PARAMETRO AND P.PADRE_PARAMETRO = 10 LEFT JOIN (SELECT * FROM SID_REG_EXPEDIENTE_DERIVACION WHERE C_ETAPA = 3) C ON A.C_NUMEROEXPEDIENTE = C.C_NUMEROEXPEDIENTE INNER JOIN SID_SEG_USUARIO D ON B.C_CODIGOUSUARIO = D.CODIGO_USUARIO INNER JOIN SID_SEG_OFICINA_DEFENSORIAL E ON E.N_ID_OD = D.N_ID_OD LEFT JOIN SID_SEG_OFICINA_DEFENSORIAL F ON F.N_ID_OD = B.N_ID_OD WHERE A.C_ESTADO = 'A'
AND TO_NUMBER(TO_CHAR(A.D_FECHAINGRESO, 'YYYYMMDD')) > TO_NUMBER(#{fechaIngresoDesdeString})-1 AND TO_NUMBER(TO_CHAR(A.D_FECHAINGRESO, 'YYYYMMDD')) < TO_NUMBER(#{fechaIngresoHastaString})+1
AND TO_NUMBER(TO_CHAR(A.D_FECHAMODIFICACION, 'YYYYMMDD')) > TO_NUMBER(#{fechaConclusionDesdeString})-1 AND TO_NUMBER(TO_CHAR(A.D_FECHAMODIFICACION, 'YYYYMMDD')) < TO_NUMBER(#{fechaConclusionHastaString})+1
AND A.C_TIPOCLASIFICACION = #{tipoExpediente}
AND A.C_TIPOINGRESO = #{canalIngreso}
AND A.C_USUREGISTRO = #{usuarioCodigo}
UNION ALL SELECT DISTINCT A.C_NUMEROEXPEDIENTE, P.NOMBRE_PARAMETRO TIPOEXPEDIENTE, B.D_FECHAACCION D_FECHAREGISTRO, C.D_FECHAACCION D_FECHAINGRESO, CASE WHEN NVL(C.N_ID_AMPLIACION, '0') = '0' THEN 'No' ELSE 'Si' END C_RESPUESTA, 'AMPLIACION' C_TIPOACCION, E.C_NOMBRECOMPLETO C_DEPENDENCIA_ORIGEN, F.C_NOMBRECOMPLETO C_DEPENDENCIA_DESTINO, B.C_CODIGOUSUARIO||B.N_ID_AMPLIACION C_CODIGOGESTION FROM SID_REG_EXPEDIENTE A INNER JOIN (SELECT * FROM SID_REG_EXPEDIENTE_AMPLIACION WHERE N_ETAPA = 1) B ON A.N_IDEXPEDIENTE = B.N_IDEXPEDIENTE INNER JOIN SID_PARAMETRO P ON A.C_TIPOCLASIFICACION = P.VALOR_PARAMETRO AND P.PADRE_PARAMETRO = 10 LEFT JOIN (SELECT * FROM SID_REG_EXPEDIENTE_AMPLIACION WHERE N_ETAPA = 3) C ON A.N_IDEXPEDIENTE = C.N_IDEXPEDIENTE INNER JOIN SID_SEG_USUARIO D ON B.C_CODIGOUSUARIO = D.CODIGO_USUARIO INNER JOIN SID_SEG_OFICINA_DEFENSORIAL E ON E.N_ID_OD = D.N_ID_OD LEFT JOIN SID_SEG_OFICINA_DEFENSORIAL F ON F.N_ID_OD = B.N_ID_AD WHERE 1 = 1
AND TO_NUMBER(TO_CHAR(A.D_FECHAINGRESO, 'YYYYMMDD')) > TO_NUMBER(#{fechaIngresoDesdeString})-1 AND TO_NUMBER(TO_CHAR(A.D_FECHAINGRESO, 'YYYYMMDD')) < TO_NUMBER(#{fechaIngresoHastaString})+1
AND TO_NUMBER(TO_CHAR(A.D_FECHAMODIFICACION, 'YYYYMMDD')) > TO_NUMBER(#{fechaConclusionDesdeString})-1 AND TO_NUMBER(TO_CHAR(A.D_FECHAMODIFICACION, 'YYYYMMDD')) < TO_NUMBER(#{fechaConclusionHastaString})+1
AND A.C_TIPOCLASIFICACION = #{tipoExpediente}
AND A.C_TIPOINGRESO = #{canalIngreso}
AND A.C_USUREGISTRO = #{usuarioCodigo}
UNION ALL SELECT DISTINCT A.C_NUMEROEXPEDIENTE, P.NOMBRE_PARAMETRO TIPOEXPEDIENTE, B.D_FECHAACCION D_FECHAREGISTRO, C.D_FECHAACCION D_FECHAINGRESO, CASE WHEN NVL(C.N_ID_SUSPENCION, '0') = '0' THEN 'No' ELSE 'Si' END C_RESPUESTA, 'SUSPENCION' C_TIPOACCION, E.C_NOMBRECOMPLETO C_DEPENDENCIA_ORIGEN, F.C_NOMBRECOMPLETO C_DEPENDENCIA_DESTINO, B.C_CODIGOUSUARIO||B.N_ID_SUSPENCION C_CODIGOGESTION FROM SID_REG_EXPEDIENTE A INNER JOIN (SELECT * FROM SID_REG_EXPEDIENTE_SUSPENCION WHERE N_ETAPA = 1) B ON A.N_IDEXPEDIENTE = B.N_IDEXPEDIENTE INNER JOIN SID_PARAMETRO P ON A.C_TIPOCLASIFICACION = P.VALOR_PARAMETRO AND P.PADRE_PARAMETRO = 10 LEFT JOIN (SELECT * FROM SID_REG_EXPEDIENTE_SUSPENCION WHERE N_ETAPA = 3) C ON A.N_IDEXPEDIENTE = C.N_IDEXPEDIENTE INNER JOIN SID_SEG_USUARIO D ON B.C_CODIGOUSUARIO = D.CODIGO_USUARIO INNER JOIN SID_SEG_OFICINA_DEFENSORIAL E ON E.N_ID_OD = D.N_ID_OD LEFT JOIN SID_SEG_OFICINA_DEFENSORIAL F ON F.N_ID_OD = B.N_ID_AD WHERE 1 = 1
AND TO_NUMBER(TO_CHAR(A.D_FECHAINGRESO, 'YYYYMMDD')) > TO_NUMBER(#{fechaIngresoDesdeString})-1 AND TO_NUMBER(TO_CHAR(A.D_FECHAINGRESO, 'YYYYMMDD')) < TO_NUMBER(#{fechaIngresoHastaString})+1
AND TO_NUMBER(TO_CHAR(A.D_FECHAMODIFICACION, 'YYYYMMDD')) > TO_NUMBER(#{fechaConclusionDesdeString})-1 AND TO_NUMBER(TO_CHAR(A.D_FECHAMODIFICACION, 'YYYYMMDD')) < TO_NUMBER(#{fechaConclusionHastaString})+1
AND A.C_TIPOCLASIFICACION = #{tipoExpediente}
AND A.C_TIPOINGRESO = #{canalIngreso}
AND A.C_USUREGISTRO = #{usuarioCodigo}

SELECT A.* FROM ( SELECT B.*, ROWNUM numfila FROM ( SELECT DISTINCT A.C_NUMEROEXPEDIENTE, B.NOMBRE_PARAMETRO TIPOEXPEDIENTE, A.D_FECHAREGISTRO, C.NOMBRE_PARAMETRO CANALINGRESO, E.C_VALOR ETAPAEXPEDIENTE, F.C_NOMBRE ESTADOCALIFICACION, G.C_NOMBRE ESTADOINVESTIGACION, H.C_NOMBRE ESTADOPERSUACION, I.C_NOMBRE ESTADOSEGUIMIENTO, CASE WHEN C_ESTADOGENERAL = 'A' THEN 'Trámite' WHEN C_ESTADOGENERAL = 'C' AND N_INDDESESTIMIENTO IS NULL THEN 'Concluido' WHEN C_ESTADOGENERAL = 'C' AND N_INDDESESTIMIENTO = 1 THEN 'Desistido' END ESTADOGENERAL, CASE N_VERSION WHEN 0 THEN 'Borrador' ELSE 'En registro' END ESTADOREGISTRO, A.C_SUMILLA, J.C_NOMBRE OD, A.D_FECHAINGRESO, A.C_USUREGISTRO, K.NOM_USUARIO||' '||K.APE_PATERNO||' '||K.APE_MATERNO USUARIONOMBRE, A.N_IDEXPEDIENTE FROM SID_REG_EXPEDIENTE A LEFT JOIN SID_PARAMETRO B ON A.C_TIPOCLASIFICACION = B.VALOR_PARAMETRO AND B.PADRE_PARAMETRO = 10 LEFT JOIN SID_PARAMETRO C ON A.C_TIPOINGRESO = C.VALOR_PARAMETRO AND C.PADRE_PARAMETRO = 20 LEFT JOIN SID_REG_ETAPA_ESTADO D ON A.N_IDEXPEDIENTE = D.N_ID_EXPEDIENTE AND D.C_INDICADOR = 'ACT' LEFT JOIN SID_REG_ETAPA E ON D.N_ID_ETAPA = E.N_ID_ETAPA LEFT JOIN SID_REG_ESTADO F ON F.N_ID_ESTADO = A.C_ESTADOCALIFICACION LEFT JOIN SID_REG_ESTADO G ON G.N_ID_ESTADO = A.C_ESTADOINVESTIGACION LEFT JOIN SID_REG_ESTADO H ON H.N_ID_ESTADO = A.C_ESTADOPERSUACION LEFT JOIN SID_REG_ESTADO I ON I.N_ID_ESTADO = A.C_ESTADOSEGUIMIENTO LEFT JOIN SID_SEG_OFICINA_DEFENSORIAL J ON A.N_CODIGOOD = J.N_ID_OD INNER JOIN SID_SEG_USUARIO K ON A.C_USUREGISTRO = K.CODIGO_USUARIO INNER JOIN SID_REG_EXPEDE_PERSONA N ON N.N_IDEXPEDIENTE = A.N_IDEXPEDIENTE INNER JOIN SID_REG_PERSONA M ON M.N_IDPERSONA = N.N_IDPERSONA WHERE A.C_ESTADO = 'A' AND N.C_TIPO IN ('01','03')
AND M.C_IDDEPA = #{idDepartamentoRecurrente}
AND M.C_IDPROV = #{idProvinciaRecurrente}
AND M.C_IDDISTR = #{idDistritoRecurrente}
AND M.C_INDDISCAPACITADO = #{esDiscapacitadoRecurrente}
AND M.C_TIPOLENGUA = #{tipoLenguaRecurrente}
AND M.C_TIPOPUEBLO = #{tipoPuebloRecurrente}
ORDER BY A.N_IDEXPEDIENTE DESC ) B ) A WHERE a.numfila BETWEEN #{ini} AND #{fin}

SELECT A.* FROM ( SELECT B.*, ROWNUM numfila FROM ( SELECT DISTINCT A.C_NUMEROEXPEDIENTE, B.NOMBRE_PARAMETRO TIPOEXPEDIENTE, A.D_FECHAREGISTRO, C.NOMBRE_PARAMETRO CANALINGRESO, E.C_VALOR ETAPAEXPEDIENTE, F.C_NOMBRE ESTADOCALIFICACION, G.C_NOMBRE ESTADOINVESTIGACION, H.C_NOMBRE ESTADOPERSUACION, I.C_NOMBRE ESTADOSEGUIMIENTO, CASE WHEN C_ESTADOGENERAL = 'A' THEN 'Trámite' WHEN C_ESTADOGENERAL = 'C' AND N_INDDESESTIMIENTO IS NULL THEN 'Concluido' WHEN C_ESTADOGENERAL = 'C' AND N_INDDESESTIMIENTO = 1 THEN 'Desistido' END ESTADOGENERAL, CASE N_VERSION WHEN 0 THEN 'Borrador' ELSE 'En registro' END ESTADOREGISTRO, A.C_SUMILLA, J.C_NOMBRE OD, A.D_FECHAINGRESO, A.C_USUREGISTRO, K.NOM_USUARIO||' '||K.APE_PATERNO||' '||K.APE_MATERNO USUARIONOMBRE, A.N_IDEXPEDIENTE FROM SID_REG_EXPEDIENTE A LEFT JOIN SID_PARAMETRO B ON A.C_TIPOCLASIFICACION = B.VALOR_PARAMETRO AND B.PADRE_PARAMETRO = 10 LEFT JOIN SID_PARAMETRO C ON A.C_TIPOINGRESO = C.VALOR_PARAMETRO AND C.PADRE_PARAMETRO = 20 LEFT JOIN SID_REG_ETAPA_ESTADO D ON A.N_IDEXPEDIENTE = D.N_ID_EXPEDIENTE AND D.C_INDICADOR = 'ACT' LEFT JOIN SID_REG_ETAPA E ON D.N_ID_ETAPA = E.N_ID_ETAPA LEFT JOIN SID_REG_ESTADO F ON F.N_ID_ESTADO = A.C_ESTADOCALIFICACION LEFT JOIN SID_REG_ESTADO G ON G.N_ID_ESTADO = A.C_ESTADOINVESTIGACION LEFT JOIN SID_REG_ESTADO H ON H.N_ID_ESTADO = A.C_ESTADOPERSUACION LEFT JOIN SID_REG_ESTADO I ON I.N_ID_ESTADO = A.C_ESTADOSEGUIMIENTO LEFT JOIN SID_SEG_OFICINA_DEFENSORIAL J ON A.N_CODIGOOD = J.N_ID_OD INNER JOIN SID_SEG_USUARIO K ON A.C_USUREGISTRO = K.CODIGO_USUARIO INNER JOIN SID_REG_EXPEDE_PERSONA N ON N.N_IDEXPEDIENTE = A.N_IDEXPEDIENTE INNER JOIN SID_REG_PERSONA M ON M.N_IDPERSONA = N.N_IDPERSONA WHERE A.C_ESTADO = 'A' AND N.C_TIPO IN ('02','03')
AND M.C_IDDEPA = #{idDepartamentoAfectado}
AND M.C_IDPROV = #{idProvinciaAfectado}
AND M.C_IDDISTR = #{idDistritoAfectado}
AND M.C_INDDISCAPACITADO = #{esDiscapacitadoAfectado}
AND M.C_TIPOLENGUA = #{tipoLenguaAfectado}
AND M.C_TIPOPUEBLO = #{tipoPuebloAfectado}
ORDER BY A.N_IDEXPEDIENTE DESC ) B ) A WHERE a.numfila BETWEEN #{ini} AND #{fin}
